	def probability(self, text, matrix):
		probability = 1.00
		row = 0
		column = 0
		firstTime = True
		for letter in text:
			if letter.isalpha() and (ord(letter.lower()) - 97) < 26:
				column = ord(letter.lower()) - 97
			else:
				continue
			if firstTime:
				row = column
				firstTime = False
			else:
				probability = probability * matrix[row][column]
		return probability
	def getLikeliest(self, text):
		languageMapper = {}
		english = self.probability(text, self.englishStochasticMatrix)
		languageMapper[english] = "english"
		spanish = self.probability(text, self.spanishStochasticMatrix)
		languageMapper[spanish] = "spanish"
		french = self.probability(text, self.frenchStochasticMatrix)
		languageMapper[french] = "french"
		german = self.probability(text, self.germanStochasticMatrix)
		languageMapper[german] = "german"
		likeliest = max(english, spanish, french, german)
		return languageMapper[likeliest]